mapper.update:
UPDATE FREEBOARD
SET	hit=hit+1
WHERE	seq = #{seq}

mapper.select:
select id,nickname,content,re_lev,re_ref,re_seq,to_char(logtime, 'YYYY.MM.DD hh:mm') as logtime
	from (select rownum rn, tt.*
	from (select * from FREEBOARD order by RE_LEV desc, RE_SEQ asc) tt)
	where RE_REF=#{seq}

mapper.update:
UPDATE NOTICEBOARD
SET	hit=hit+1
WHERE	seq = #{seq}

mapper.select:
select id,nickname,content,re_lev,re_ref,re_seq,to_char(logtime, 'YYYY.MM.DD hh:mm') as logtime
	from (select rownum rn, tt.*
	from (select * from NOTICEBOARD order by RE_LEV desc, RE_SEQ asc) tt)
	where RE_REF=#{seq}

mapper.update:
UPDATE QNABOARD
SET	hit=hit+1
WHERE	seq = #{seq}

mapper.select:
select seq, category, id, nickname, subject, content ,file1 ,re ,re_ref ,re_seq ,hit ,to_char(logtime, 'YYYY.MM.DD hh:mm') as logtime 
		 from  (select rownum rn, tt.* 
		 from (select * from QNABOARD order by seq asc) tt)   
		 where rn>=#{startNum} and rn<=#{endNum}
